#!/usr/bin/env bash
#
# This file is generated by l2tdevtools update-dependencies.py any dependency
# related changes should be made in dependencies.ini.

# Exit on error.
set -e

# Dependencies for running dfvfs, alphabetized, one per line.
# This should not include packages only required for testing or development.
PYTHON2_DEPENDENCIES="PyYAML
                      libbde-python
                      libewf-python
                      libfsntfs-python
                      libfvde-python
                      libfwnt-python
                      libqcow-python
                      libsigscan-python
                      libsmdev-python
                      libsmraw-python
                      libvhdi-python
                      libvmdk-python
                      libvshadow-python
                      libvslvm-python
                      python-backports-lzma
                      python-crypto
                      python-dfdatetime
                      python-dtfabric
                      python-pysqlite
                      python-pytsk3";

# Additional dependencies for running dfvfs tests, alphabetized,
# one per line.
TEST_DEPENDENCIES="python-mock";

# Additional dependencies for doing dfvfs development, alphabetized,
# one per line.
DEVELOPMENT_DEPENDENCIES="python-sphinx
                          pylint";

# Additional dependencies for doing dfvfs debugging, alphabetized,
# one per line.
DEBUG_DEPENDENCIES="libbde-debuginfo
                    libewf-debuginfo
                    libfsntfs-debuginfo
                    libfvde-debuginfo
                    libfwnt-debuginfo
                    libqcow-debuginfo
                    libsigscan-debuginfo
                    libsmdev-debuginfo
                    libsmraw-debuginfo
                    libvhdi-debuginfo
                    libvmdk-debuginfo
                    libvshadow-debuginfo
                    libvslvm-debuginfo";

sudo dnf install dnf-plugins-core
sudo dnf copr -y enable @gift/dev
sudo dnf install -y ${PYTHON2_DEPENDENCIES}

if [[ "$*" =~ "include-debug" ]]; then
    sudo dnf install -y ${DEBUG_DEPENDENCIES}
fi

if [[ "$*" =~ "include-development" ]]; then
    sudo dnf install -y ${DEVELOPMENT_DEPENDENCIES}
fi

if [[ "$*" =~ "include-test" ]]; then
    sudo dnf install -y ${TEST_DEPENDENCIES}
fi
